SELECT SAM_Results.treatment AS Treatment, SAM_Results.control AS Control, Genes.name as Name, SAM_Results.Name_Long, SAM_Results.Rank,  SAM_Results.p_value, SAM_Results.q_value, SAM_Results.R_fold, Ebayes_Results.logFC, Ebayes_Results.AveExpr, Ebayes_Results.t, Ebayes_Results.p_value, Ebayes_Results.adj_p_value, Ebayes_Results.B, Ebayes_Results.Rank, KeggInfo.ecNum, KeggInfo.definition, KeggInfo.catalyticActivity, KeggInfo.cofactors, KeggInfo.associateDiseases, KeggInfo.pathway,  KeggInfo.pathway_class , Go_Annotation.goAcc, Go_Annotation.term, Go_Annotation.goTermType, KogInfo.kogid, KogInfo.kogdefline, KogInfo.kogClass FROM SAM_Results JOIN Genes ON Genes.name = SAM_Results.name JOIN Ebayes_Results ON Genes.name = Ebayes_Results.name LEFT JOIN KeggInfo ON Genes.transcriptId = KeggInfo.transcriptId  LEFT JOIN Go_Annotation ON Genes.transcriptId = Go_Annotation.transcriptId LEFT JOIN KogInfo ON Genes.transcriptId = KogInfo.transcriptId WHERE SAM_Results.treatment = Ebayes_Results.treatment AND SAM_Results.control = Ebayes_Results.control ORDER BY SAM_Results.treatment, SAM_Results.control, SAM_Results.Rank